import React, {Component} from 'react';
import {ListGroup, ListGroupItem, Col, Row, Button, Form, FormControl, FormGroup} from 'react-bootstrap';
import './CnvOverview.css';

export default class CnvDetail extends Component {
   constructor(props) {
      super(props);
      let cnvId = this.props.location.pathname.split('/')[2];
      //this.setState({people: this.state.people.push(this.props.getPerson(msg.prsId))}
      this.props.updateMsgs(cnvId, 
         () => this.props.Msgs.forEach(msg => {
            this.props.getPerson(msg.prsId, (res) => {
               var temp = this.state.people;
               console.log('res ',res);
               temp.push(res);
               console.log('temp',temp)
               this.setState({people: temp})
            })


           // console.log(this.state.people.push(this.props.getPerson(msg.prsId)))
         }))
      this.state = {
         showModal: false,
         showConfirmation: false,
         newMessage: '',
         cnvId: cnvId,
         people: []
      }
   }

   getValidationState = () => {
      if (this.state.newMessage) {
         return null;
      }
      return "warning";
   }

   formSubmit = (event) => {
      this.props.addMsg({ content: this.state.newMessage }, this.state.cnvId);
      console.log(this.state.newMessage);
   }

   handleChange = (e) => {
      this.setState({newMessage: e.target.value});
   }

   render() {
      console.log("Rerendering Cnv Detail ", this.state.messages);


      var messages = this.props.Msgs;
      console.log(messages)
      console.log(this.state.people)
      var msgItems = [];
      if(this.state.people.length === messages.length){
         messages.forEach((msg, idx) => {
            msgItems.push(<MsgItem
                  key={msg.id}
                  content={msg.content}
                  id={msg.id}
                  email={this.state.people[idx].email}
                  whenMade={msg.whenMade}
                  //showControls={cnv.ownerId === this.props.Prss.id}
                  onDelete={() => this.openConfirmation(msg)}
                  onEdit={() => this.openModal(msg)} />);
         });
      }


      return (
         <section className="container">
            <h1>Conversation Detail</h1>
            <Row>
               <Col sm={4}>
                  Content
               </Col>
               <Col sm={4}>
                  Email
               </Col>
               <Col sm={4}>
                  Date
               </Col>
            </Row>

            <ListGroup>
               {msgItems}
            </ListGroup>

            <Form>

               <FormGroup controlId="formBasicText"
                  validationstate={this.getValidationState()}
               >
                  <Form.Label>New Message: </Form.Label>
                  <FormControl
                     type="text"
                     value={this.state.newMessage}
                     placeholder="Enter text"
                     onChange={this.handleChange}
                  />
                  <FormControl.Feedback />
                  {
                     !(this.state.newMessage) && 
                     <Form.Text className="text-muted">
                        A new message is required
                     </Form.Text>
                  }

               </FormGroup>

               <FormGroup>
                  <Col>
                     <Button onClick={this.formSubmit}>
                        Create New Message
                     </Button>
                 </Col>
               </FormGroup>

            </Form>



         </section>
      )
   }
}

//Create a MsgItem Component to show each message. This component shows on a top line the posting date and email of the poster, 
//and beneath that line the text content of the message, whose appearance is toggled on and off by clicking on the top line.

const MsgItem = function (props) {
   return (
      <ListGroupItem>
         <Row>


            <Col sm={4}>
               {props.content}
            </Col>
            <Col sm={4}>
               {props.email}
            </Col>
            <Col sm={4}>{new Intl.DateTimeFormat('us',
               {
                  year: "numeric", month: "short", day: "numeric",
                  hour: "2-digit", minute: "2-digit", second: "2-digit"
               })
               .format(new Date(props.whenMade))}
            </Col>

         </Row>
      </ListGroupItem>
   )
}
