import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import {ListGroup, ListGroupItem, Col, Row, Button} from 'react-bootstrap';
import CnvModal from './CnvModal';
import {ConfDialog} from '../components';
//import {delCnv} from '../../api';
import './CnvOverview.css';

export default class CnvDetail extends Component {
   constructor(props) {
      super(props);
      this.state = {
         showModal: false,
         showConfirmation: false,
      }
   }

   // Open a model with a |cnv| (optional)
   openModal = (cnv) => {
      this.setState({showModal: true, editCnv: cnv});
   }

   modalDismiss = (result) => {
      if (result.status === "Ok") {
         if (this.state.editCnv)
            this.modCnv(result);
         else
            this.newCnv(result);
      }
      this.setState({ showModal: false, editCnv: null });
   }

   modCnv(result) {
      this.props.modCnv(this.state.editCnv.id, result.title);
      this.props.updateCnvs();
   }

   newCnv(result) {
      this.props.addCnv({ title: result.title });
   }

   openConfirmation = (cnv) => {
      this.setState({ delCnv: cnv, showConfirmation: true })
   }

   closeConfirmation = (res) => {
      if(res === "Yes"){
         this.props.delCnv(this.state.delCnv.id);
         this.props.updateCnvs();
         this.setState({showConfirmation: false, delCnv: null });
      } else {
         console.log(res)
         this.setState({ showConfirmation: false, delCnv: null });
      }

   }

   openEdit = (cnv) => {
      this.setState({ delCnv: cnv, showConfirmation: true })
   }

   render() {
      console.log("Rerendering Cnv Detail ", this.props);

      var messages = this.props.updateMsgs();
      var people = []
      messages.forEach(msg => people.push(this.props.getPerson(msg.prsId)))

      var msgItems = [];
      console.log(messages);
      messages.forEach((msg, idx) => {
         msgItems.push(<MsgItem
               key={msg.id}
               content={msg.content}
               id={msg.id}
               email={people[idx].email}
               showControls={cnv.ownerId === this.props.Prss.id}
               onDelete={() => this.openConfirmation(msg)}
               onEdit={() => this.openModal(msg)} />);
      });

      return (
         <section className="container">
            <h1>Conversation Detail</h1>
            <ListGroup>
               {msgItems}
            </ListGroup>
            <Button variant="primary" onClick=
               {() => this.openModal()}>New Conversation</Button>
            {/* Modal for creating and change cnv */}
            <CnvModal
               showModal={this.state.showModal}
               //show={this.state.showEdit}
               title={this.state.editCnv ? "Edit title" : "New Conversation"}
               cnv={this.state.editCnv}
               onDismiss={this.modalDismiss} />
            <ConfDialog
               show={this.state.showConfirmation}
               title="Delete Conversation"
               body={`Are you sure you want to delete the Conversation
           '${this.state.delCnv ? this.state.delCnv.title : ''}'`}
               buttons={['Yes', 'Abort']}
               onClose={this.closeConfirmation} />
         </section>
      )
   }
}

//Create a MsgItem Component to show each message. This component shows on a top line the posting date and email of the poster, 
//and beneath that line the text content of the message, whose appearance is toggled on and off by clicking on the top line.

const MsgItem = function (props) {
   return (
      <ListGroupItem>
         <Row>

            <Col sm={4}>{new Intl.DateTimeFormat('us',
               {
                  year: "numeric", month: "short", day: "numeric",
                  hour: "2-digit", minute: "2-digit", second: "2-digit"
               })
               .format(new Date(props.whenMade))}
            </Col>
            <Col sm={4}>
               props.title
            </Col>
            <Col sm={4}>
               props.email
            </Col>

         </Row>
      </ListGroupItem>
   )
}

// A Cnv list item
const CnvItem = function (props) {
   return (
      <ListGroupItem>
         <Row> 
            <Col sm={4}>
               <Link to={"/CnvDetail/" + props.id}>{props.title}</Link>
            </Col>
            {
               props.lastMessage ? 

               <Col sm={4}>{new Intl.DateTimeFormat('us',
               {
                  year: "numeric", month: "short", day: "numeric",
                  hour: "2-digit", minute: "2-digit", second: "2-digit"
               })
               .format(new Date(props.lastMessage))}
               </Col> 
               : 
               <Col sm={4}> Null </Col>
            }

            {props.showControls ?
               <div className="float-right">
                  <Button size="sm" onClick={props.onDelete}>
                     <span className="fa fa-thumbs-up"/>
                  </Button>
                  <Button size="sm" onClick={props.onEdit}>
                     <span className="fa fa-edit"/>
                  </Button>
               </div>
               : ''}
         </Row>
      </ListGroupItem>
   )
}
