const baseURL = "http://localhost:3001/";
const headers = new Headers();
var sessionId;

headers.set('Content-Type', 'application/JSON');
//headers.set("Access-Control-Allow-Credentials", true);
//headers.set("Access-Control-Allow-Headers", "*");
