import React, {Component} from 'react';
import {ListGroup, ListGroupItem, Col, Row, Button, Form, FormControl, FormGroup} from 'react-bootstrap';
import {MsgItem, ConfDialog, CnvModal} from '../components';

import './CnvOverview.css';

export default class CnvDetail extends Component {
   constructor(props) {
      super(props);
      let cnvId = this.props.location.pathname.split('/')[2];
      //this.setState({people: this.state.people.push(this.props.getPerson(msg.prsId))}
      this.props.updateMsgs(cnvId)
      this.state = {
         newMessage: '',
         cnvId: cnvId,
         showModal: false
      }
   }

   getValidationState = () => {
      if (this.state.newMessage) {
         return null;
      }
      return "warning";
   }

   formSubmit = (event) => {
      this.props.addMsg({ content: this.state.newMessage }, this.state.cnvId,
         () => {

            this.props.updateMsgs(this.state.cnvId,
               () => {this.setState({newMessage: ''})})

         });
      console.log(this.state.newMessage);
   }

   formValid = () => {
      return this.state.newMessage.length
   }

   handleChange = (e) => {
      this.setState({newMessage: e.target.value});
   }

   hideContent (e) {
      console.log(e)
      console.log(this)
   }

   closeErrorConf = (btn) => {
      this.props.clearErrors();
   }

   openModal = (cnv) => {
      this.setState({showModal: true, editCnv: cnv});
   }

   modalDismiss = (result) => {
      if (result.status === "Ok") {
         if (this.state.editCnv)
            this.modCnv(result);
         else
            this.newCnv(result);
      }
      this.setState({ showModal: false, editCnv: null });
   }

   render() {
      console.log("Rerendering Cnv Detail ", this.state.messages);


      var messages = this.props.Msgs;
      var msgItems = [];

      if(messages.length){
         messages.forEach((msg, idx) => {
            msgItems.push(<MsgItem
                  key={msg.content}
                  content={msg.content} // <- use content here bc a newly added
                  id={msg.id}           // msg does not get its id returned 
                  email={msg.email}     // as per REST Spec
                  whenMade={msg.whenMade}
                  hideContent={() => this.hideContent(this)}
                  ishidden={0}
                  onDelete={() => this.openConfirmation(msg)}
                  onEdit={() => this.openModal(msg)} />);
         });
      }


      return (
         <section className="container">
            <h1>Conversation Detail</h1>
            <Row hidden={this.props.Msgs.length === 0}>

               <Col sm={4}>
                  Email
               </Col>
               <Col sm={4}>
                  Date
               </Col>
            </Row>


            <ListGroup>
               {msgItems}
            </ListGroup>

            <Form>

               <FormGroup controlId="formBasicText"
                  validationstate={this.getValidationState()}
               >
                  <Form.Label>New Message: </Form.Label>
                  <FormControl
                     type="text"
                     value={this.state.newMessage}
                     placeholder="Enter text"
                     onChange={this.handleChange}
                  />
                  <FormControl.Feedback />
                  {
                     !(this.state.newMessage) && 
                     <Form.Text className="text-muted">
                        A new message is required
                     </Form.Text>
                  }

               </FormGroup>

               <FormGroup>
                  <Col>
                     <Button onClick={this.formSubmit} disabled={!this.formValid()}>
                        Create New Message
                     </Button>
                 </Col>
               </FormGroup>

            </Form>

            <CnvModal
               showModal={this.state.showModal}
               //show={this.state.showEdit}
               title={this.state.editCnv ? "Edit title" : "New Conversation"}
               cnv={this.state.editCnv}
               onDismiss={this.modalDismiss} />

            <ConfDialog
               show={this.props.Errs.length !== 0}
               title="Error Notice"
               body={this.props.Errs[0]}
               buttons={['For Sure']}
               onClose={this.closeErrorConf}
                />

         </section>
      )
   }
}

//Create a MsgItem Component to show each message. This component shows on a top line the posting date and email of the poster, 
//and beneath that line the text content of the message, whose appearance is toggled on and off by clicking on the top line.
